# creates a function that lemmatize words in the files and filter A and B level words out them, then stores results in the dictionary
def filter(file):
    with open(file, "r", encoding="utf-8-sig") as txt:
        data = txt.readlines()
        txt.close()

    rtext = ''.join(data)  # convert list to string
    npattern = r'[^\uAC00-\uD7AF\u0020-\u0023\u0027-\u002A\u002C-\u002F]'   # remove all non-hangul and non-punctuation
    text = re.sub(npattern, ' ', rtext)


    # creates separate lists for different parts of speech
    nouns = mecab.nouns(text)
    adverbs = []
    predicates = []

    pos = mecab.pos(text)
    for w in pos:
        if w[1] == 'VA' or w[1] == 'VV' or w[1] == 'VX' or w[1] == 'VCP' or w[1] == 'VCN':
            predicates.append(w[0] + '다')
        if w[1] == 'MAG' or w[1] == 'MM':
            adverbs.append(w[0])

    lemmas = nouns + adverbs + predicates
    tokens_full.append(lemmas)

    # EXCLUDE WORDS OF DIFFERENT LEVELS. IMPORTANT; IF YOU WANT TO EXCLUDE ONLY BEGINNER(A) VOCABULARY, LEAVE ONLY FILTER A ACTIVE AND PUT FILTERA IN THE BRACKETS IN THE LINES 77 AND 79
    #IF YOU WANT TO EXCLUDE BEGINNER AND INTERMEDIATE VOCABULARY, LEAVE FOLTERA AND FILTERB ACTIVE AND PUT FILTERB IN THE BRACKETS IN THE LINES 77 AND 79. AND SO ON.
    filterA = [i for i in lemmas if i not in listA]
    filterB = [i for i in filterA if i not in listB]
    filterC = [i for i in filterB if i not in listC]
    filterAVL = [i for i in filterC if i not in AVL]

    # add all the not AB words to the separate list, to count item frequency after
    freqlist.append(filterAVL)
    # delete repeating words from the list
    filter = list(dict.fromkeys(filterAVL))


    # add name of the sub-field as key and words as a value to a dictionary
    filter_sub_corpus.add(file, filter)
    sub_corpus_tokens.add(file, lemmas)
    print(file)


