#function that counts percentage
def percentage(part, whole):
    return 100 * float(part) / float(whole)

# function that counts the number of tokens and types covered by each list (out of corpus)

def stats(list):
    tokens = len(list)
    types = len(Counter(list).keys())
    tok_coverage = percentage(tokens, tokens_total)
    types_coverage = percentage(types, types_total)
    return ([tokens, tok_coverage, types, types_coverage])

